import React,{useState} from "react";
import { Form,Button } from "react-bootstrap";

function Register() {
  const dispatch = useDispatch();
  const [useInfo, setUserInfo] = useState({ name:"",email: "", password: "" });
  const handleChange = (event) => {
    setUserInfo({ ...useInfo, [event.target.name]: event.target.value });
  };
  const onSubmit = (event) => {
    event.preventDefault();
    dispatch(login(useInfo, navigate));
    setUserInfo({name:"", email: "", password: "" });
  };
  return (
    <Form style={{ width: "40%", margin: "auto" }}>
      <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
        <Form.Label>name</Form.Label>
        <Form.Control type="text" onChange={handleChange} placeholder="type your name"  />
      </Form.Group>
      <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
        <Form.Label>Email address</Form.Label>
        <Form.Control type="email" onChange={handleChange} placeholder="name@example.com" />
      </Form.Group>
      <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
        <Form.Label>password</Form.Label>
        <Form.Control type="password" onChange={handleChange} placeholder="type your password" />
      </Form.Group>
      <Button variant="primary" type="submit">
        Submit
      </Button>
    </Form>
  );
}

export default Register;
